import React, { useEffect, useState } from 'react';
import { client } from '../prismic-config';

const Homepage = () => {
    const [prismicData, setPrismicData] = useState({ Homepage: null });

    // Get the homepage documents from Prismic
    useEffect(() => {
      const fetchPrismicData = async () => {
        try {
          const homeDoc = await client.getSingle('homepage',{ lang });
          if (homeDoc) {
            console.log(homeDoc)
            setPrismicData({ Homepage: homeDoc.results.data});
          } else {
            console.warn('Blog Home document was not found. Make sure it exists in your Prismic repository');
          }
        } catch (error) {
          console.error(error);
        }
      }

      fetchPrismicData();
    }, []);

    return (
        <div>
            <h1>Hello Prismic</h1>
        </div>
      );
    }


  export default Homepage;
