import React, { useEffect, useState } from 'react';
import { client,linkResolver } from '../../prismic-config';
import { RichText , Link} from 'prismic-reactjs';
//import NextLink from 'next/link'



const MenuLinks = ({lang, uid}) => {
  const [menuData, setMenuData] = useState(null);

  if (!uid)
    {uid ="";}
  // Get the homepage documents from Prismic
  useEffect(() => {
    const fetchPrismicData = async () => {
      try {
        const menuDoc = await client.getSingle('menu', {lang});
        if (menuDoc) {
          setMenuData(menuDoc);
        } else {
          console.warn('Page document was not found. Make sure it exists in your Prismic repository');
        }
        } catch (error) {
          console.error(error);
        }
    }

      fetchPrismicData();
  }, [lang]);

  function createAltLink(altLang){
    return "/"+altLang+"/"+uid;
  }
  if (menuData) {
      return ( 
          <nav>
          <ul>
            {menuData.data.menu_links.map (item =>
            <li>
                <a href={Link.url(item.link, linkResolver)}>
                    {RichText.asText(item.label)}
                </a>
            </li>
            )}
            {menuData.alternate_languages.map(altLang=>
              <li key={altLang.id}>
                {/* <NextLink
                  as={linkResolver(altLang)}
                  href={linkResolver(altLang)}
                  passHref
                  prefetch
                >
                <a className={"flag-icon flag-icon-" + altLang.lang.slice(-2)}></a>
                </NextLink> */}
                <a href = {createAltLink(altLang.lang)} className={"flag-icon flag-icon-" + altLang.lang.slice(-2)}></a>
              </li>

            )}
          </ul>
          </nav>


        );







  } else {
      return null;
  }

}


export default MenuLinks;
