import React, { useEffect, useState } from 'react';
import { client } from '../prismic-config';
import SliceZone from '../components/sliceZone';


const Homepage = () => {
  const [prismicData, setPrismicData] = useState({ Homepage: null });

  // Get the homepage documents from Prismic
  useEffect(() => {
    const fetchPrismicData = async () => {
      try {
        const homeDoc = await client.getSingle('homepage', { lang: 'en-gb' });
        if (homeDoc) {
        console.log(homeDoc.lang)
        console.log(homeDoc.data.page_content)
          setPrismicData({ Homepage: homeDoc.data});
        } else {
          console.warn('Blog Home document was not found. Make sure it exists in your Prismic repository');
        }
        } catch (error) {
          console.error(error);
        }
    }

      fetchPrismicData();
  }, []);

  if (prismicData.Homepage) {
      console.log("hello ",prismicData.Homepage.page_content)
    const data = prismicData.HomePage.page_content;

    return (
      <div>
        <SliceZone sliceZone={ data } />
      </div>
    );
  } else {
      return null
  }
   return null
}


export default Homepage;
