import React, { useEffect, useState } from 'react';
import { client } from '../prismic-config';
import SliceZone from '../components/sliceZone';


const Homepage = ({match}) => {
  const [prismicData, setPrismicData] = useState(null);


  const  {lang}  = this.match;
  console.log("lang  ", lang);
  // Get the homepage documents from Prismic
  useEffect(() => {
    const fetchPrismicData = async () => {
      try {
        const homeDoc = await client.getSingle('homepage', {lang});
        if (homeDoc) {
          setPrismicData(homeDoc.data);
        } else {
          console.warn('Blog Home document was not found. Make sure it exists in your Prismic repository');
        }
        } catch (error) {
          console.error(error);
        }
    }

      fetchPrismicData();
  }, []);

  if (prismicData) {
    const data = prismicData.page_content;
    console.log (data);
    return (
      <div className = "container">
        <SliceZone sliceZone={ data } />
      </div>
    );
  } else {
      return null;
  }

}


export default Homepage;
