import React from 'react';

const FullWidthImage = ({ slice }) => {
  if (slice.primary.background_image_position) {
    var backgroundImage = `full-width-image-${slice.primary.background_image_position}`
  } else {
      var backgroundImage = "full-width-image-right"
  }
  //- If there is no white theme image then render the black image 
if (slice.primary.image_white.url) {
  var whiteImage = slice.primary.image_white
} else {
  var whiteImage = slice.primary.image_black
}

//- If there is no black theme image then render white image 
if (slice.primary.image_black.url) {
  var blackImage = slice.primary.image_black
} else {
  var blackImage = slice.primary.image_white
}
  return (
    <section className = "image-container">
      <div className = {backgroundImage}>
      <img className = "back-img" src={slice.primary.background_image.url} alt={slice.primary.background_image.alt} />
      </div>
      <div className = "main-image">
        <img className = "dark-asset" src={blackImage.url} alt = {blackImage.alt}/>
        <img className = "white-asset" src={whiteImage.url} alt = {whiteImage.alt}/>
      </div>
    </section>
  );
};

export default FullWidthImage;
