import React, { useEffect, useState } from 'react';
import { client,linkResolver } from '../../prismic-config';


const MenuLinks = ({}) => {
  const [menuData, setMenuData] = useState(null);


  // Get the homepage documents from Prismic
  useEffect(() => {
    const fetchPrismicData = async () => {
      try {
        const menuDoc = await client.getSingle('menu');
        if (menuDoc) {
          setMenuData(menuDoc);
        } else {
          console.warn('Page document was not found. Make sure it exists in your Prismic repository');
        }
        } catch (error) {
          console.error(error);
        }
    }

      fetchPrismicData();
  }, []);

  if (menuData) {
      console.log(menuData.data.menu_links)
      return ( 
          <ul>
              {menuData.data.menu_links.map(item=><li><a href={item.Link.url(item.link, linkResolver)}>{Richtext.asText(item.label)}</a></li>)}

          </ul>



        );


  } else {
      return null;
  }

}


export default MenuLinks;
